test.describe('Performance', () => {
  const pages = [
    { path: '/', name: 'Home', lcpBudget: 2000 },
    { path: '/corpus', name: 'Corpus', lcpBudget: 2500 },
    { path: '/corpus/transcript-batch-1', name: 'Document', lcpBudget: 3000 },
  ];

  for (const { path, name, lcpBudget } of pages) {
    test(`${name} page meets performance budgets`, async ({ page }) => {
      // Start measuring
      await page.goto(path);

      // Collect metrics via Performance API
      const metrics = await page.evaluate(() => {
        const paint = performance.getEntriesByType('paint');
        const nav = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;

        return {
          fcp: paint.find(p => p.name === 'first-contentful-paint')?.startTime,
          ttfb: nav.responseStart - nav.requestStart,
          domContentLoaded: nav.domContentLoadedEventEnd - nav.startTime,
          load: nav.loadEventEnd - nav.startTime,
        };
      });

      // Get LCP via PerformanceObserver
      const lcp = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          new PerformanceObserver((list) => {
            const entries = list.getEntries();
            const lastEntry = entries[entries.length - 1];
            resolve(lastEntry.startTime);
          }).observe({ type: 'largest-contentful-paint', buffered: true });

          // Fallback timeout
          setTimeout(() => resolve(0), 10000);
        });
      });

      console.log(`${name} Performance:`, {
        ...metrics,
        lcp,
      });

      // Assert budgets
